package com.praveen;

public class SwaptwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=11;
		System.out.println("Before swapping numbers: "+a+" "+b);
		 int temp=a;
		 a=b;
		 b=temp;
		 System.out.println("After swapping numbers: "+a+" "+b);
	}

}
